INSERT INTO product (productcode, subproductcode, version, defined_by, activated, category_id, product_type, descriptive_name, description, provider, frequency_id, date_format, scale_factor, scale_offset, nodata, mask_min, mask_max, unit, data_type_id, masked) VALUES ('vgt_ndvi', 'ndv', 'undefined', 'JRC', 1, 'vegetation', 'Ingest', 'Normalized Difference Vegetation Index', 'The NDVI can be used to measure and monitor plant growth, vegetation cover, and biomass production. It is a dimensionless variable.  The values vary between -1 and 1. Increasing positive NDVI values indicate increasing amounts of green vegetation. Moderate values represent shrub and grassland (0.2 to 0.3), while high values indicate temperate and tropical rainforests (0.6 to 0.8). NDVI values near zero and decreasing negative values indicate non-vegetated features such as barren surfaces (rock and soil) and water, snow, ice, and clouds.', 'VITO/DevCoCast - JRC', 'undefined', 'YYYYMMDD', 0.00100000000000000002, 0, -32768, NULL, NULL, '', 'Int16', 1);
