INSERT INTO product (productcode, subproductcode, version, defined_by, activated, category_id, product_type, descriptive_name, description, provider, frequency_id, date_format, scale_factor, scale_offset, nodata, mask_min, mask_max, unit, data_type_id, masked) VALUES ('vgt_bbdhrn', 'vgt_bbdhrn_native', 'undefined', 'JRC', 0, 'radiationflux', 'Native', 'Near infrared albedo', 'It corresponds to the amount of solar energy (spectrally integrated over Near infrared spectral domain) reflected by a surface and provides information on the radiative balance, thus on temperature and water balance. The albedo is one of the most interesting variable required as a primary input for global circulation models used to forecast short term weather and long term climatic change. It is also mandatory for local to regional estimates of energy and mass exchanges between the earth surface and the atmosphere as described by soil vegetation atmosphere transfer models (SVAT).', 'VITO/DevCoCast', 'undefined', 'YYYYMMDD', 0, 0, 0, NULL, NULL, '', 'undefined', 1);
INSERT INTO product (productcode, subproductcode, version, defined_by, activated, category_id, product_type, descriptive_name, description, provider, frequency_id, date_format, scale_factor, scale_offset, nodata, mask_min, mask_max, unit, data_type_id, masked) VALUES ('vgt_ndwi', 'vgt_ndwi_native', 'undefined', 'JRC', 0, 'vegetation', 'Native', 'Normalized Difference Water Index', 'The NDWI can be used to assess and monitor water content in a normalised way, and to detect drought stress. NDWI values exhibit a quicker response to drought conditions than NDVI. It is a dimensionless variable. The  values vary between -1 and 1. The NDWI increases with vegetation water content or from dry soil to free water.', 'VITO/DevCoCast', 'undefined', 'YYYYMMDD', 0, 0, 0, NULL, NULL, '', 'undefined', 0);
